tinymce.PluginManager.add('pastezen', function(editor, url) {
	// Add a button that opens a window
	editor.addButton('pastezen', {
		text: 'zen',
		icon: false,
		onclick: function() {
			// Open window
			editor.windowManager.open({
				title: 'tinyMCE:zen',
				url: url + '/pastezen.php',
				width: 800,
				height: 600,
				buttons: [{
						text: 'Close',
						onclick: 'close'
					}]
			});
		}
	});
	// Adds a menu item to the tools menu
	editor.addMenuItem('pastezen', {
		text: 'pasteZen',
		context: 'tools',
		onclick: function() {
			// Open window with a specific url
			editor.windowManager.open({
				title: 'tinyMCE:zen',
				url: url + '/pastezen.php',
				width: 800,
				height: 600,
				buttons: [{
						text: 'Close',
						onclick: 'close'
					}]
			});
		}
	});
});